class Solution {
public:
    int minimumCost(vector<int>& st, vector<int>& tr, vector<vector<int>>& sr) { 
        map<pair<int,int>,vector<pair<int,pair<int,int>>>> gr;

        int si = st[0],sj = st[1], ti=tr[0],tj=tr[1];
        gr[{si,sj}].push_back({abs(ti-si)+abs(tj-sj),{ti,tj}});

        for(int i = 0 ; i < sr.size(); i++)
        {
            int u1i = sr[i][0],u1j = sr[i][1],v1i = sr[i][2],v1j = sr[i][3], c = sr[i][4];

            gr[{si,sj}].push_back({abs(u1i-si)+abs(u1j-sj),{u1i,u1j}});
            gr[{si,sj}].push_back({abs(v1i-si)+abs(v1j-sj),{v1i,v1j}});

            gr[{u1i,u1j}].push_back({c,{v1i,v1j}});

            gr[{u1i,u1j}].push_back({abs(ti-u1i)+abs(tj-u1j),{ti,tj}});
            gr[{v1i,v1j}].push_back({abs(ti-v1i)+abs(tj-v1j),{ti,tj}});

            for(int j = i+1; j < sr.size(); j++)
            {
                int u2i = sr[j][0],u2j = sr[j][1],v2i = sr[j][2],v2j = sr[j][3], c2 = sr[j][4];

                gr[{u1i,u1j}].push_back({abs(u1i-u2i)+abs(u1j-u2j),{u2i,u2j}});
                gr[{u2i,u2j}].push_back({abs(u1i-u2i)+abs(u1j-u2j),{u1i,u1j}});

                gr[{v1i,v1j}].push_back({abs(v1i-u2i)+abs(v1j-u2j),{u2i,u2j}});
                gr[{u2i,u2j}].push_back({abs(v1i-u2i)+abs(v1j-u2j),{v1i,v1j}});

                gr[{u1i,u1j}].push_back({abs(u1i-v2i)+abs(u1j-v2j),{v2i,v2j}});
                gr[{v2i,v2j}].push_back({abs(u1i-v2i)+abs(u1j-v2j),{u1i,u1j}});

                gr[{v1i,v1j}].push_back({abs(v1i-v2i)+abs(v1j-v2j),{v2i,v2j}});
                gr[{v2i,v2j}].push_back({abs(v1i-v2i)+abs(v1j-v2j),{v1i,v1j}});

            }
        }


        map<pair<int,int>,int> vis;
        map<pair<int,int>,int> dis;

        priority_queue<pair<int,pair<int,int>>,vector<pair<int,pair<int,int>>>, greater<pair<int,pair<int,int>>>> pq;

        pq.push({0,{si,sj}});

        while(!pq.empty())
        {
            auto best = pq.top();
            pq.pop();

            int d = best.first;
            auto cord = best.second;

            if(vis[cord]==1) continue;

            dis[cord]=d;
            vis[cord]=1;

            for(auto ch : gr[cord])
            {
                if(vis[ch.second]==0)
                {
                    pq.push({d+ch.first,ch.second});
                }
            }
        }
        return dis[{ti,tj}];
    }
};
