var minimumCost = function(start, target, specialRoads) {
    let result = target[0] - start[0] + target[1] - start[1];

    const stack = [];
    const dist = new Array(specialRoads.length);
    let index = 0;

    for (const [x1, y1, x2, y2, c] of specialRoads) {
        dist[index] = x1 - start[0] + y1 - start[1];
        stack.push([x2, y2, c + dist[index++]]);
    }

    let x, y, cost;

    while (stack.length) {
        [x, y, cost] = stack.shift();
        result = Math.min(result, cost + target[0] - x + target[1] - y);
        index = 0;
        for (const [x1, y1, x2, y2, c] of specialRoads) {
            if (dist[index] > (cost + Math.abs(x1 - x) + Math.abs(y1 - y))) {
                dist[index] = cost + Math.abs(x1 - x) + Math.abs(y1 - y);
                stack.push([x2, y2, c + dist[index]]);
            }
            ++index;
        }
    }

    return result;
};
```
