        while(!q.empty()){

            //Get Current point and its cost till that point
            auto [point,costToThisPoint] = q.front();
            q.pop();

            //If this point has greater cost than previous minimum cost no need to update and process further
            if(costToThisPoint > (minCost.count(point)>0?minCost[point]:INT_MAX )) continue;

            //else update costToThisPoint as the minCost
            minCost[point] = costToThisPoint;

            //Then shortestPath to reach targetPos from this point is minCost till this point from previous all paths + manhattanDistance to targetPos
            shortestPath = min(shortestPath , minCost[point] + manhattanDistance(point, targetPos));

            //explore all specialRoads and updates minCost to reach endOfTheRoad
            for(auto v : specialRoads){
                pair<int,int> startOfRoad = {
                    v[0],
                    v[1]
                };

                pair<int,int> endOfRoad = {
                    v[2],
                    v[3]
                };

                int roadCost = v[4];

                //to reach roadEnd = cost till my current point + cost to reach start of Road + roadCost
                int costToEndOfRoad = costToThisPoint + manhattanDistance(point,startOfRoad) + roadCost;

                //If costToReach EndofTheRoad is greater than previous minimum cost, no need to update and process further
                if(costToEndOfRoad >= (minCost.count(endOfRoad)>0?minCost[endOfRoad]:INT_MAX )) continue;

                //else update cost to reach endOfRoad
                minCost[endOfRoad] = costToEndOfRoad;

                //push endOfRoad, to start exploring from endOfRoad
                q.push({endOfRoad, minCost[endOfRoad]});
            }


        }

        return shortestPath;
    }
};
