class Solution {
public:
    unordered_map<int, unordered_map<int, vector<pair<pair<int, int>, long long>>  > >   g;

    unordered_map<int, unordered_map<int, long long> > dis;


    void createGraph(vector<vector<int>>& specialRoads) {
        for (auto specialRoad : specialRoads) {
            int src_x = specialRoad[0];
            int src_y = specialRoad[1];

            g[src_x][src_y].push_back({{specialRoad[2], specialRoad[3]}, specialRoad[4]});
        }
    }

    struct cmp {
        bool operator()(const pair<pair<int, int>, long long>& a, const pair<pair<int, int>, long long>& b) {
            return a.second > b.second;
        }
    };

    int getdis(const pair<int ,int>& start, const pair<int, int>& target) {
        long long d = 0;

        d += abs(start.first - target.first) + abs(start.second - target.second);

        return d;
    }

    bool isExistent(int r, int c) {
        return dis.find(r) != dis.end() && dis[r].find(c) != dis[r].end();
    }

    int minimumCost(vector<int>& start, vector<int>& target, vector<vector<int>>& specialRoads) {
        createGraph(specialRoads);

        priority_queue<pair<pair<int, int>, long long>, vector<pair<pair<int, int>, long long>>, cmp> q;

        q.push({{start[0], start[1]}, 0});

        dis[start[0]][start[1]] = 0;

        while (!q.empty()) {
            pair<pair<int, int>, int> node = q.top();
            q.pop();
            int r = node.first.first;
            int c = node.first.second;

            long long wt = node.second;
            //cout<<r<<" "<<c<<" "<<wt<<endl;

            for (auto neigh : g[r][c]) {
                int n_r = neigh.first.first;
                int n_c = neigh.first.second;

                long long n_wt = neigh.second;

                if (!isExistent(n_r, n_c) || wt + n_wt < dis[n_r][n_c]) {
                    q.push({{n_r, n_c}, wt + n_wt});
                    dis[n_r][n_c] = wt + n_wt;
                }
            }

            for (auto specialRoad: specialRoads) {
                int n_r = specialRoad[0];
                int n_c = specialRoad[1];
                long long t_dis = getdis({r, c}, {specialRoad[0], specialRoad[1]});

                if (!isExistent(n_r, n_c) || t_dis + wt < dis[n_r][n_c]) {
                    q.push({{n_r, n_c}, t_dis + wt});
                    dis[n_r][n_c] = t_dis + wt;
                }
            }

            long long t_dis = getdis({r, c}, {target[0], target[1]});

            int n_r = target[0];
            int n_c = target[1];

            if (!isExistent(n_r, n_c) || t_dis + wt < dis[target[0]][target[1]]) {
                q.push({{n_r, n_c}, t_dis + wt});
                dis[n_r][n_c] = t_dis + wt;
            }
        }

        return dis[target[0]][target[1]];
    }
};
