class Solution {
public:
    int minimumCost(vector<int>& start, vector<int>& target, vector<vector<int>>& specialRoads) {
        if(start==target){
            return 0;
        }

        priority_queue<vector<int>, vector<vector<int>>, greater<vector<int>>> pq;
        unordered_map<int,unordered_map<int,int>> visited;
        pq.push({0,start[0],start[1]});

        while(!pq.empty()){
            vector<int> curr = pq.top();
            pq.pop();
            int dis = curr[0];
            int r = curr[1];
            int c = curr[2];
            if(r==target[0] && c==target[1]){
                return dis;
            }
            if(visited.count(r) && visited[r].count(c)){
                if(visited[r][c]<=dis){
                    continue;
                }
            }
            visited[r][c] = dis;
            // straight to target
            int toTarget = abs(r-target[0]) + abs(c-target[1]);
            pq.push({dis+toTarget,target[0],target[1]});
            //if could to specialRoads
            for(auto sr:specialRoads){
                int x1=sr[0];
                int y1=sr[1];
                int x2=sr[2];
                int y2=sr[3];
                int cost=sr[4];

                if(r==x1 && c==y1){ // could use specialRoads
                    int nextCost = cost + dis;
                    pq.push({nextCost,x2,y2});
                }else{
                    int nextCost = abs(r-x1) + abs(c-y1) + dis;
                    pq.push({nextCost,x1,y1});
                }
            }
        }
        return -1;

    }
};
