class Solution {
    public int minimumCost(int[] start, int[] target, int[][] specialRoads) {
        Map<List<Integer>, Integer> points = new HashMap<>();
        Map<Integer, Map<Integer, Integer>> edges = new HashMap<>();
        List<Integer> startPoint = new ArrayList<>(Arrays.asList(start[0], start[1]));
        List<Integer> targetPoint = new ArrayList<>(Arrays.asList(target[0], target[1]));
        int s = insertPoint(points, startPoint);
        int t = insertPoint(points, targetPoint);
        for (int[] road : specialRoads) {
            List<Integer> beginPoint = new ArrayList<>(Arrays.asList(road[0], road[1]));
            List<Integer> endPoint = new ArrayList<>(Arrays.asList(road[2], road[3]));
            int b = insertPoint(points, beginPoint);
            int e = insertPoint(points, endPoint);
            addEdge(b, e, road[4], edges);
        }
        for (List<Integer> ps : points.keySet()) {
            for (List<Integer> pe : points.keySet()) {
                addEdge(points.get(ps), points.get(pe), getDistance(ps, pe), edges);
            }
        }
        return getShortestDistance(s, t, edges);
    }

    private int getShortestDistance(int s, int t, Map<Integer, Map<Integer, Integer>> edges) {
        Set<Integer> visited = new HashSet<>();
        Map<Integer, Integer> distances = new HashMap<>();
        for (var entry : edges.entrySet()) {
            distances.put(entry.getKey(), Integer.MAX_VALUE / 2);
            for (var key : entry.getValue().keySet()) {
                distances.put(key, Integer.MAX_VALUE / 2);
            }
        }
        distances.put(s, 0);
        while (!visited.contains(t)) {
            int next = getSmallestUnvistedKey(distances, visited);
            Map<Integer, Integer> nextNeighbors = edges.containsKey(next) ? 
                edges.get(next) : new HashMap<>();
            for (int nbr : nextNeighbors.keySet()) {
                if (visited.contains(next)) {
                    continue;
                }
                int newDistance = distances.get(next) + nextNeighbors.get(nbr);
                int currentDistance = distances.get(nbr);
                if (newDistance < currentDistance) {
                    distances.put(nbr, newDistance);
                }
            }
            visited.add(next);
        }
        return distances.get(t);
    }

    private int getSmallestUnvistedKey(Map<Integer, Integer> map, Set<Integer> visited) {
        int smallestValue = Integer.MAX_VALUE, smallestKey = -1;        
        for (Map.Entry<Integer, Integer> entry : map.entrySet()) {
            if (entry.getValue() < smallestValue && !visited.contains(entry.getKey())) {
                smallestValue = entry.getValue();
                smallestKey = entry.getKey();
            }
        }
        return smallestKey;
    }

    private int insertPoint(Map<List<Integer>, Integer> points, List<Integer> point) {
        if (points.containsKey(point)) {
            return points.get(point);
        }
        points.put(point, points.size());
        return points.get(point);
    }

    private void addEdge(int s, int t, int weight, Map<Integer, Map<Integer, Integer>> edges) {
        if (s == t) {
            return;
        }
        if (!edges.containsKey(s)) {
            edges.put(s, new HashMap<>());
        }
        Map<Integer, Integer> sourceMap = edges.get(s);
        if (sourceMap.containsKey(t) && sourceMap.get(t) <= weight) {
            return;
        }
        sourceMap.put(t, weight);
    }

    private int getDistance(List<Integer> p1, List<Integer> p2) {
        return Math.abs(p1.get(0) - p2.get(0)) + Math.abs(p1.get(1) - p2.get(1));
    }
}
