public class Solution {
    long T = 100000L;
    public int Dis(int x1, int y1, int x2, int y2)=>Math.Abs(x2-x1) + Math.Abs(y2-y1);
    public long Key(int x, int y) => x* T + y;

    public int MinimumCost(int[] start, int[] target, int[][] specialRoads) {

        int M  = target.Sum()-start.Sum();
        Dictionary<long, int> Visited = new(); //keep visited position, postion=>cost
        PriorityQueue<(int x, int y, int cost),int> Q = new ();//D ar
        var specialRoadss = specialRoads.Where(sr=>Math.Abs(sr[2]-sr[0])+Math.Abs(sr[3]-sr[1])>sr[4]);


        if(specialRoadss.Count()==0)
            return M;

        Visited[Key(start[0] , start[1])]  =  0;
        Visited[Key(target[0], target[1])] =  M;

        foreach(int[] sr in specialRoadss)
        {
            long key =Key(sr[2],sr[3]);
            var distance = Math.Min(Dis(sr[0],sr[1],start[0],start[1]) + sr[4],Dis(start[0],start[1],sr[2],sr[3]));
            if( Visited.ContainsKey(key) )
                 Visited[key] = Math.Min(distance,Visited[key]) ; 
            else
                 Visited[key] = distance;

            Q.Enqueue((sr[2],sr[3],Visited[key]), Visited[key] );

        }
        Q.Enqueue((target[0],target[1],M),M);

        while(Q.Count>0)
        {

            (int x, int y,int cost) = Q.Dequeue();
            long key = Key(x,y);
            if(Visited[key]<cost)
                continue;
            int distance = Visited[key] + Dis(x,y, target[0],target[1]);
            if(distance<M)
            {

               M = distance;
            }


           foreach(int[] sr in specialRoadss)
           {               
                distance = Dis(sr[0],sr[1],x,y) + sr[4] + Visited[key];
                var key1 = Key(sr[2],sr[3]);


                if(distance<M && distance<Visited[key1])
                {

                    Visited[key1] = distance;
                    Q.Enqueue((sr[2],sr[3],distance), Visited[key1] );

                }
           }
        }

       return M; 
    }
}
