class Cord{
    int x,y,c;
    Cord(int x,int y,int c){
        this.x=x;
        this.y=y;
        this.c=c;
    }
}
class Solution {
    public int minimumCost(int[] start, int[] target, int[][] specialRoads) {
        List<Integer> l=new ArrayList<>();
        for(int i=0;i<specialRoads.length;i++){
            if(Math.abs(specialRoads[i][0]-specialRoads[i][2])+Math.abs(specialRoads[i][1]-specialRoads[i][3])>=specialRoads[i][4])   l.add(i);
        }
        int minCost=Math.abs(start[0]-target[0])+Math.abs(start[1]-target[1]),n=1;
        Queue<Cord> q=new PriorityQueue<>((e1,e2)->e1.c-e2.c);
        q.add(new Cord(start[0],start[1],0));
        Set<String> vis=new HashSet<>();
        while(!q.isEmpty() && minCost>q.peek().c){
            Cord c=q.poll();
            if(c.x==target[0] && c.y==target[1])    return c.c;
            String s=c.x+" "+c.y;
            if(vis.contains(s)) continue;
            vis.add(s);
            for(int i:l){
                if(c.x==specialRoads[i][0] && c.y==specialRoads[i][1]){
                    int cost=specialRoads[i][4]+c.c;
                    if(!vis.contains(specialRoads[i][2]+" "+specialRoads[i][3])){
                        Cord nc=new Cord(specialRoads[i][2],specialRoads[i][3],cost);
                        q.add(nc);
                    }
                }
                else{
                    int cost=Math.abs(c.x-specialRoads[i][0])+Math.abs(c.y-specialRoads[i][1])+c.c;
                    if(!vis.contains(specialRoads[i][0]+" "+specialRoads[i][1])){
                        Cord nc=new Cord(specialRoads[i][0],specialRoads[i][1],cost);
                        q.add(nc);
                    }
                }
            }
            q.add(new Cord(target[0],target[1],Math.abs(c.x-target[0])+Math.abs(c.y-target[1])+c.c));
        }
        return minCost;
    }
}
