typedef long long ll;
class Solution {
public:
    int minimumCost(vector<int>& start, vector<int>& target, vector<vector<int>>& roads) 
    {
        int n=roads.size();
        vector<ll>cost(n,INT_MAX);
        for(int i=0;i<n;i++)
            for(int j=0;j<n;j++)
            {
                ll tmp=abs(start[0]-roads[j][0])+abs(start[1]-roads[j][1])+roads[j][4];
                for(int k=0;k<n;k++)
                {
                    ll cur=cost[k]+abs(roads[k][2]-roads[j][0])+abs(roads[k][3]-roads[j][1])+roads[j][4];
                    tmp=min(cur,tmp);
                }
                cost[j]=min(cost[j],1ll*tmp);
            }
        ll res=abs(target[0]-start[0])+abs(target[1]-start[1]);
        for(int i=0;i<n;i++)
        {
            ll cur=cost[i]+abs(target[0]-roads[i][2])+abs(target[1]-roads[i][3]);
            res=min(res,cur);
        }
        return res;
    }
};
