	cost = {(e[2], e[3]):abs(e[2]-s[0])+abs(e[3]-s[1]) for e in roads}
	q, ans = [[0, s[0], s[1]]], abs(t[0]-s[0])+abs(t[1]-s[1])
	while(q):
		c, x, y = heappop(q)
		ans = min(ans, c+abs(t[0]-x)+abs(t[1]-y))
		for x1, y1, x2, y2, d in roads:
			if abs(x1-x2)+abs(y1-y2)>d and cost[(x2, y2)]>c+abs(x-x1)+abs(y-y1)+d:
				cost[(x2, y2)] = c+abs(x-x1)+abs(y-y1)+d
				heappush(q, [cost[(x2, y2)], x2, y2])
	return ans
