class comp {
public:
  bool operator()(pair<int, int>& lhs, pair<int, int>& rhs){
      if(lhs.second > rhs.second) {
          return true;
      }
      return false;
  }
};
class Solution {
public:
    string pos_to_key(int x, int y) {
        return to_string(x) + ":" + to_string(y);
    }

    void construct_order(
    vector<pair<int, int>>& order_map,
    vector<int>& start, vector<int>& target,
     vector<vector<int>>& specialRoads) {
       unordered_set<string> seen;

       order_map.push_back(pair<int, int>(start[0], start[1]));

       seen.insert(pos_to_key(start[0], start[1]));
       seen.insert(pos_to_key(target[0], target[1]));
       for(int idx = 0; idx < specialRoads.size(); idx++) {
           vector<int> road = specialRoads[idx];
           string s_key = pos_to_key(road[0], road[1]);
           string e_key = pos_to_key(road[2], road[3]);
           if(seen.find(s_key) == seen.end()) {
               order_map.push_back(pair<int, int>(road[0], road[1]));
               seen.insert(s_key);
           }
           if(seen.find(e_key) == seen.end()) {
               order_map.push_back(pair<int, int>(road[2], road[3]));
               seen.insert(e_key);
           }
       }

       order_map.push_back(pair<int, int>(target[0], target[1]));


    }

    int dijkstra_find_shortest(vector<vector<int>>& graph, int node_count, int start, int end) {
        vector<int> distance(node_count, INT_MAX);
        distance[start] = 0;
        priority_queue<pair<int, int>, vector<pair<int, int>>, comp> node_queue;
        node_queue.push(pair<int, int>(start, 0));
        while(node_queue.empty() == false) {
            // every time you pop the smallest node 
            pair<int ,int> front_node = node_queue.top();
            node_queue.pop();
            int node_idx = front_node.first;
            int cur_dis = front_node.second;
            vector<int> neighbors = graph[node_idx];
            for(int idx = 0; idx < node_count; idx++) {
                if(idx == node_idx) {
                    continue;
                }
                int direct_dis = graph[node_idx][idx];
                int candidate_dis = direct_dis + cur_dis;
                if(candidate_dis < distance[idx]) {
                    // update or call it relax the edge
                    distance[idx] = candidate_dis;
                    // push the new node
                    node_queue.push(pair<int, int>(idx, candidate_dis));
                }
            }
        }
        return distance[end];
    }

    int minimumCost(vector<int>& start, vector<int>& target,
     vector<vector<int>>& specialRoads) {
        vector<pair<int, int>> order_map;
        construct_order(order_map, start, target, specialRoads);
        int count_nodes = order_map.size();
      //  cout << "num of nodes:" << count_nodes << endl;
        // construct the graph
        // first reverse order
        unordered_map<string, int> reverse_order_map;
        for(int idx = 0; idx < order_map.size(); idx++) {
            string key = pos_to_key(order_map[idx].first, order_map[idx].second);
            reverse_order_map[key] = idx;
        }

        vector<vector<int>> graph(count_nodes, vector<int>(count_nodes, INT_MAX));
        for(int i = 0; i < count_nodes; i++) {
            for(int j = i; j < count_nodes; j++) {
                if(i == j) {
                    graph[i][j] = 0;
                }
                else {
                    // you calculate the distance
                    pair<int, int> p1 = order_map[i];
                    pair<int, int> p2 = order_map[j];
                    int dis = abs(p1.first - p2.first) + abs(p1.second - p2.second);
                    graph[i][j] = dis;
                    graph[j][i] = dis;
                }
            }
        }

        // use the special road
        for(int idx = 0; idx < specialRoads.size(); idx++) {
            vector<int> road = specialRoads[idx];
            string s_key = pos_to_key(road[0], road[1]);
            string e_key = pos_to_key(road[2], road[3]);
            int s_idx = reverse_order_map[s_key];
            int e_idx = reverse_order_map[e_key];
            int cost = road[4];
            if(graph[s_idx][e_idx] > cost) {
                graph[s_idx][e_idx] = cost;
            }
        }

       /* for(int i = 0; i < count_nodes; i++) {
            for(int j = 0; j < count_nodes; j++) {
                cout << graph[i][j] << ",";
            }
            cout<<endl;
        } */

        return dijkstra_find_shortest(graph, count_nodes, 0, count_nodes - 1);
    }
};
