 * @param {number[]} target
 * @param {number[][]} specialRoads
 * @return {number}
 */
const Heap = function(n, empty, compare) {
    this.data = Array(n).fill(empty);
    this.end = 0;
    this.empty = empty;
    this.compare = compare;
}

Heap.prototype.insert = function(val) {
    const end = this.end;
    const compare = this.compare;
    this.data[end] = val;
    if (end != 0) {
        let i = end;
        while(i > 0 && !compare(this.data[Math.floor((i-1)/2)], this.data[i])) {
            let j = Math.floor((i-1)/2);
            [this.data[j], this.data[i]] = [this.data[i], this.data[j]];
            i = j;
        }
    }
    this.end++;
}
Heap.prototype.poll = function() {
    const compare = this.compare;
    let end = this.end;
    if(end == 0) {
        return null;
    } else {
        end--;
        var output = this.data[0];
        if (end >= 0) {
            this.data[0] = this.data[end];
            let i = 0;
            let j = 2*i + 1;
            let k = 2*i + 2;
            while(!compare(this.data[i], this.data[j]) || !compare(this.data[i], this.data[k])) {
                let l = compare(this.data[j], this.data[k])? j : k;
                [this.data[i], this.data[l], i, j, k] = [this.data[l], this.data[i], l, 2*l + 1, 2*l + 2];
            }            
        }
    }
    this.end--;
    this.data[end] = this.empty;    
    return output;
}
var minimumCost = function(start, target, specialRoads) {
    if(start.join(" ") == target.join(" ")) {
        return 0;
    }    
    const getCost = function(initial, terminal) {
        const [x1, y1] = initial;
        const [x2, y2] = terminal;
        return Math.abs(x1-x2) + Math.abs(y1-y2);
    }    
    const buildGraph = function(start, target, edges) {
        const nodeList = new Map();
        const nodeByType = [new Set(), new Set()];        
        nodeList.set(start.join(" "), 0);
        nodeList.set(target.join(" "), 1);
        let i = 2;
        let pairing = 0;
        const edgeList = [];
        for(let j = 0; j < edges.length; j++) {
            const [x1, y1, x2, y2, cost] = edges[j];
            const [init, term] = [[x1, y1] .join(" "), [x2, y2].join(" ")];
            const nodes = Array(2).fill();
            for(let n = 0; n < 2; n++) {
                const pt = [init,term][n];
                let k = nodeList.get(pt);
                if (k == undefined) {
                    k = i;
                    i++;
                }
                nodeByType[n].add([pt, pairing]);
                nodeList.set(pt, k);
                nodes[n] = k;
            }
            edgeList.push([nodes[0], nodes[1], cost])
            pairing++;                        
        }

        edgeList.push([0,1, getCost(start, target)]);

        nodeByType[0].forEach(pt => {
            const node = nodeList.get(pt[0]);
            const pt1 = pt[0].split(" ");
            const coordinates = Array.from(pt1, s => Number(s));
            edgeList.push([0, node, getCost(start, coordinates)]);
        })

        nodeByType[1].forEach(pt => {
            const node = nodeList.get(pt[0]);
            const pt1 = pt[0].split(" ");
            const coordinates = Array.from(pt1, s => Number(s));
            edgeList.push([node,1, getCost(target, coordinates)]);
        })

        nodeByType[1].forEach(init => {
            nodeByType[0].forEach(term => {
                if(init[1] != term[1]) {
                    const initNode = nodeList.get(init[0]);
                    const termNode = nodeList.get(term[0]);                    
                    const init1 = Array.from(init[0].split(" "), s => Number(s));
                    const term1 = Array.from(term[0].split(" "), s => Number(s));
                    edgeList.push([initNode,termNode, getCost(init1, term1)]);
                }
            })
        })
        const output = Array.from(Array(nodeList.size), () => []);
        edgeList.forEach(edge => {
            const [a,b,cost] = edge;
            output[a].push([b,cost]);
        })
        return output; 
    }
    const graph = buildGraph(start, target, specialRoads);
    const dist = Array(graph.length).fill(Number.POSITIVE_INFINITY);
    dist[0] = 0;
    const visited = new Set();
    const heap = new Heap(400*400, [null, Number.POSITIVE_INFINITY], (a,b) => a[1] <= b[1]);
    heap.insert([0,0]);
    while(heap.end > 0) {
        const [a, currDist] = heap.poll();
        if(!visited.has(a)) {
            visited.add(a);
            graph[a].forEach(edge => {
                const [b, cost] = edge;
                dist[b] = Math.min(dist[b], cost + currDist);
                heap.insert([b,dist[b]]);
            });
        }
    }
    return dist[1];     
};
