class Solution {
public:
    typedef tuple<int,int,int> triple_int;
    int minimumCost(vector<int>& start, vector<int>& target, vector<vector<int>>& sr) {
        // So the solution seem to be a modified dijkstra's.
        // BFS + priority queue. The visited data structure should remove redundant traversals.
        // Need to preprocess the special roads for bfs.
        unordered_map<int, unordered_map<int, vector<vector<int>>>> special_roads;
        for(int i = 0; i < sr.size(); i++){
            special_roads[sr[i][0]][sr[i][1]].push_back({sr[i][2], sr[i][3], sr[i][4]});
        }

        unordered_map<int, unordered_set<int>> visited;
        priority_queue<triple_int, vector<triple_int>, std::greater<triple_int>> pq;
        pq.push(tuple{0, start[0], start[1]});
        while(!pq.empty()){
            auto cur = pq.top();
            pq.pop();
            int cost = get<0>(cur), x = get<1>(cur), y = get<2>(cur);
            if(visited[x].find(y) != visited[x].end()) continue;
            visited[x].insert(y);
            // cout << "At x: " << x << " y: " << y << " cost: " << cost << endl;
            // Found the destination.
            if(x == target[0] && y == target[1]){
                return cost;
            }

            // Lets add non special edges from current point.
            // First add target.
            pq.push(tuple{cost + abs(x - target[0]) + abs(y - target[1]), target[0], target[1]});

            // Next add start co-ordinate of special roads.
            for(int i = 0; i < sr.size(); i++){
                if(visited[sr[i][0]].find(sr[i][1]) == visited[sr[i][0]].end()){
                    pq.push(tuple{cost + abs(x - sr[i][0]) + abs(y - sr[i][1]), sr[i][0], sr[i][1]});
                }
            }

            // If we are already at start of a special road. use special road cost.
            for(auto& road : special_roads[x][y]){
                if(visited[road[0]].find(road[1]) == visited[road[0]].end()){
                   pq.push(tuple{cost + road[2], road[0], road[1]}); 
                }
            }
        }


        // Should never reach here.
        return -1;
    }
};
```

