It might be straightforward to come up with the solution but the implementation can be quite tricky 

Steps I followed to make my life easier while implementaion 
 - Find all the veriticies that need to be included in the Dijkstra implementaion.
 - Find all edges for each of these vertices note here we need to include edges from special road as well as edges which are adjacent to very node pair.
 - Ones edges and vertices are ready apply Dijkstra
 - Note: I tried to use Bellman–Ford algorithm but got TLE  

```
func minimumCost(start []int, target []int, specialRoads [][]int) int {
	seen := make(map[string]bool)
	vertices := make([][]int, 0)
	vertices = append(vertices, start)
	vertices = append(vertices, target)
	seen[key(start)] = true
	seen[key(target)] = true

	edges := make(map[string][][]int)

	for _, road := range specialRoads {
		v1, v2, _ := separate(road)
		if _, ok := seen[key(v1)]; !ok {
			seen[key(v1)] = true
			vertices = append(vertices, v1)
		}
		if _, ok := seen[key(v2)]; !ok {
			seen[key(v2)] = true
			vertices = append(vertices, v2)
		}
	}

	for _, v := range vertices {
		edges[key(v)] = make([][]int, 0)
	}

	for _, road := range specialRoads {
		v1, v2, edge := separate(road)
		edges[key(v1)] = append(edges[key(v1)], []int{v2[0], v2[1], edge})
	}

	for _, s := range vertices {
		for _, t := range vertices {
			if key(s) == key(t) {
				continue
			}
			edges[key(s)] = append(edges[key(s)], []int{t[0], t[1], dist(s, t)})
		}
	}

	var distances Arrays
	cost := make(map[string]int)
	for _, s := range vertices {
		if key(s) == key(start) {
			distances = append(distances, IndexedVal{
				index: key(s),
				val:   0,
			})
			cost[key(s)] = 0
		} else {
			distances = append(distances, IndexedVal{
				index: key(s),
				val:   math.MaxInt,
			})
			cost[key(s)] = math.MaxInt
		}
	}
	heap.Init(&distances)
	for distances.Len() != 0 {
		currentMin := heap.Pop(&distances).(IndexedVal)
		if currentMin.index == key(target) {
			return currentMin.val
		}
		for _, edge := range edges[currentMin.index] {
			to := []int{edge[0], edge[1]}
			weight := edge[2]
			if currentMin.val+weight < cost[key(to)] {
				cost[key(to)] = currentMin.val + weight
				heap.Push(&distances, IndexedVal{index: key(to), val: cost[key(to)]})
			}
		}
	}
	return -1
}

func dist(start, target []int) int {
	return Abs(start[0]-target[0]) + Abs(target[1]-start[1])
}

func key(a []int) string {
	return fmt.Sprintf("%d,%d", a[0], a[1])
}

func separate(start []int) ([]int, []int, int) {
	return []int{start[0], start[1]}, []int{start[2], start[3]}, start[4]
}


func Abs(a int) int {
	if a < 0 {
		return -a
	}
	return a
}


//heap stuff
type IndexedVal struct {
	index string
	val   int
}

func (i IndexedVal) String() string {
	return fmt.Sprintf("value: %d, index: %d", i.val, i.index)
}

type Arrays []IndexedVal

func (a *Arrays) Len() int {
	return len(*a)
}

func (a *Arrays) Less(i, j int) bool {
	return (*a)[i].val <= (*a)[j].val
}

func (a *Arrays) Swap(i, j int) {
	(*a)[i], (*a)[j] = (*a)[j], (*a)[i]
}

func (a *Arrays) Push(x interface{}) {
	*a = append(*a, IndexedVal{
		index: x.(IndexedVal).index,
		val:   x.(IndexedVal).val,
	})
}

func (a *Arrays) Pop() interface{} {
	lastVal := (*a)[len(*a)-1]
	*a = (*a)[:len(*a)-1]
	return lastVal
}
