        [targetArgId]: targetArgId === startArgId ? 0 : Number.MAX_SAFE_INTEGER,
    }

    const queue = new MinPriorityQueue({ priority: (e) => e.min });
    queue.enqueue({ node: startArgId, min: 0 });

