//Dijkstra's algorithm
//Edges: special roads, and manhantan path to start position of special roads
class Solution {
    //combine x/y coordinates as point id for hashing
    long w = 100001; // weight of x
    long pid(vector<int>const& p) { return p[0]*w+p[1]; }
    long pid(int x, int y) { return x*w+y; }
public:
    int minimumCost(vector<int>& start, vector<int>& target, vector<vector<int>>& specialRoads) {
        int startX = start[0], startY = start[1], targetX = target[0], targetY = target[1]; 
        unordered_map<long, vector<pair<long, int>>> rd = {{pid(target), {{0, 0}}}}; 
        for (auto& x : specialRoads) {
            if(abs(x[0] - x[2]) + abs(x[1] - x[3]) > x[4])
                rd[pid(x)].emplace_back(pid(x[2],x[3]), x[4]); 
        } 

        unordered_map<long, int> dist; 
        dist[pid(start)] = 0; 
        priority_queue<pair<int, long>, vector<pair<int, long>>, greater<>> pq; 
        pq.emplace(0, pid(start)); 
        while (pq.size()) {
            auto [d, p] = pq.top(); pq.pop(); 
            if (p == pid(target)) return d; 
            for (auto const& [P, cost] : rd[p]) {
                if (!dist.count(P) || d+cost < dist[P]) {
                    dist[P] = d + cost;
                    pq.emplace(d+cost, P); 
                }
            }
            for (auto const& [P, _] : rd) {
                int D = d + abs(P%w - p%w) + abs(P/w - p/w); 
                if (!dist.count(P) || D < dist[P]) {
                    dist[P] = D; 
                    pq.emplace(D, P); 
                }
            }
        }
        return -1; 
    }
};
