            int direct_cost = cost + dist(x, y, target[0], target[1]);
            pq.push({direct_cost, {target[0], target[1]}});
            for(int i = 0; i < n; i++){
                auto &point = edges[i];
                int reach = cost + dist(x, y, point[0], point[1]);
                if(reach >= visited[i]) continue;
                visited[i] = reach;
                int tmp_cost = reach + point[4];
                pq.push({tmp_cost, {point[2], point[3]}});
            }
        }

        return res;
    }
};
