class Solution {
public:
    int cost(int x1,int y1,int x2,int y2){
        return (abs(x2-x1)+abs(y2-y1));
    }
    int minimumCost(vector<int>& start, vector<int>& target, vector<vector<int>>& specR){
        set<pair<int,int>>st;
        for(auto it:specR){
            st.insert({it[0],it[1]});
            st.insert({it[2],it[3]});
        }
        st.insert({start[0],start[1]});
        st.insert({target[0],target[1]});
        int i=0;
        map<pair<int,int>,int>mp;
        for(auto it:st){
            mp[it]=i++;
        }
        int s1=mp[{start[0],start[1]}];
        int e1=mp[{target[0],target[1]}];
        vector<pair<int,int>>adj[i];
        adj[s1].push_back({e1,cost(start[0],start[1],target[0],target[1])});
        for(auto it:specR){
            int sps=mp[{it[0],it[1]}];
            int spe=mp[{it[2],it[3]}];
            int wt=it[4];
            adj[s1].push_back({sps,cost(start[0],start[1],it[0],it[1])});
            adj[spe].push_back({e1,cost(it[2],it[3],target[0],target[1])});
            adj[sps].push_back({spe,wt});

            for(auto x:specR){
                if(x==it)continue;
                int spss=mp[{x[0],x[1]}];
                int spee=mp[{x[2],x[3]}];
                adj[spe].push_back({spss,cost(it[2],it[3],x[0],x[1])});
                adj[spee].push_back({sps,cost(x[2],x[3],it[0],it[1])});
            }
        }
        priority_queue<pair<int,int>,vector<pair<int,int>>,greater<pair<int,int>>>pq;

        pq.push({0,s1});
        vector<int>dist(i,INT_MAX);
        dist[s1]=0;
        while(!pq.empty()){
            auto it=pq.top();
            pq.pop();
            int dis=it.first;
            int node=it.second;
            if(node==e1){
                return dis;
            }
            for(auto x:adj[node]){
                int nnode=x.first;
                int ndis=x.second;
                if(dist[nnode]>dist[node]+ndis){
                    dist[nnode]=dist[node]+ndis;
                    pq.push({dist[nnode],nnode});
                }
            }
        }
        return 0;
    }
};



