typedef pair<int,int> pii;
class Solution {
public:
    int cost(int x1,int y1,int x2,int y2)
    {
        return (abs(x1-x2)+abs(y1-y2));
    }
    int minimumCost(vector<int>& start, vector<int>& target, vector<vector<int>>& sr) {
        set<pii>st;
        for (auto it: sr)
        {
            st.insert({it[0],it[1]});
            st.insert({it[2],it[3]});
        }
        st.insert({start[0],start[1]});
        st.insert({target[0],target[1]});
        map<pii,int>mp;
        int cnt=0;
        for (auto it:st)
        {
            mp[it]=cnt++;
        }
        vector<pii>adj[cnt];
        int sInd=mp[{start[0],start[1]}];
        int eInd=mp[{target[0],target[1]}];
        adj[sInd].push_back({eInd,cost(start[0],start[1],target[0],target[1])});
        for ( auto it:sr)
        {
            int so=mp[{it[0],it[1]}];
            int se=mp[{it[2],it[3]}];
            int price=it[4];

            adj[sInd].push_back({so,cost(start[0],start[1],it[0],it[1])});
            adj[so].push_back({se,price});
            adj[se].push_back({eInd,cost(it[2],it[3],target[0],target[1])});

            for (auto it2: sr)
            {
                if (it==it2) continue;
                 int soo=mp[{it2[0],it2[1]}];
                 int see=mp[{it2[2],it2[3]}];
                 adj[se].push_back({soo,cost(it[2],it[3],it2[0],it2[1])});
                 adj[see].push_back({so,cost(it2[2],it2[3],it[0],it[1])});
            }
        }
        priority_queue<pii,vector<pii>,greater<pii>>pq;
        vector<int>dist(cnt,INT_MAX);
        dist[sInd]=0;
        pq.push({0,sInd});
        while(!pq.empty())
        {
            auto temp=pq.top();
            pq.pop();
            int d=temp.first;
            int node=temp.second;
            if (node==eInd) return d;
            for (auto it:adj[node])
            {
                if (dist[it.first]>dist[node]+it.second)
                {
                    dist[it.first]=dist[node]+it.second;
                    pq.push({dist[node]+it.second,it.first});
                }
            }
        }
        return 1;

    }
};
