class Solution {


public:
    int minimumCost(vector<int>& s, vector<int>& t, vector<vector<int>>& v) {
    int n=2*v.size()+7;

    vector<pair<int,int>>adj[n];

    map<vector<int>,int>h;

    vector<vector<int>>p;
    int dis[n];

        for(int i=0;i<n;i++)dis[i]=1e9;
        dis[0]=0;
        p.push_back({s[0],s[1]});
        p.push_back({t[0],t[1]});

    for(auto i:v){
          p.push_back({i[0],i[1]});
          p.push_back({i[2],i[3]});
// map given path nodes to minimum of all such paths
            if(h[{i[0],i[1],i[2],i[3]}]!=0)
        h[{i[0],i[1],i[2],i[3]}]=min(h[{i[0],i[1],i[2],i[3]}],i[4]);
        else h[{i[0],i[1],i[2],i[3]}]=i[4];
        }



        for(int i=0;i<p.size();i++){
          for(int j=i+1;j<p.size();j++){
              int  wt=abs(p[j][0]-p[i][0])+abs(p[j][1]-p[i][1]);


              if(h[{p[i][0],p[i][1],p[j][0],p[j][1]}]!=0)  // if front way is mapped then take minimum of absolute path vs speacial path
              {
                  adj[i].push_back({j,min(wt,h[{p[i][0],p[i][1],p[j][0],p[j][1]}])});

              }
              else 
              {
                  adj[i].push_back({j,wt});

              }

              if(h[{p[j][0],p[j][1],p[i][0],p[i][1]}]!=0)  //if reverse way is mapped then take minimum of absolute path vs speacial path
              {
                  adj[j].push_back({i,min(wt,h[{p[j][0],p[j][1],p[i][0],p[i][1]}])});

              }
              else 
              {
                 adj[j].push_back({i,wt});

              }              
          }
        }

        //Normal Djikstra
        priority_queue<pair<int,int>,vector<pair<int,int>>,greater<pair<int,int>>>q;
        q.push({0,0});
        while(q.size()){
            int pa_dis=q.top().first, pa=q.top().second;
            q.pop();

            for(auto i:adj[pa]){
                int ch=i.first,wt=i.second;
                if(dis[ch]>pa_dis+wt){
                    dis[ch]=pa_dis+wt;
                    q.push({dis[ch],ch});
                }
            }
        }

        return dis[1];



    }
};
