              if(h[{p[j][0],p[j][1],p[i][0],p[i][1]}]!=0)  //if reverse way is mapped then take minimum of absolute path vs speacial path
              {
                  adj[j].push_back({i,min(wt,h[{p[j][0],p[j][1],p[i][0],p[i][1]}])});

              }
              else 
              {
                 adj[j].push_back({i,wt});

              }              
          }
        }

        //Normal Djikstra
        priority_queue<pair<int,int>,vector<pair<int,int>>,greater<pair<int,int>>>q;
        q.push({0,0});
        while(q.size()){
            int pa_dis=q.top().first, pa=q.top().second;
            q.pop();

            for(auto i:adj[pa]){
                int ch=i.first,wt=i.second;
                if(dis[ch]>pa_dis+wt){
                    dis[ch]=pa_dis+wt;
                    q.push({dis[ch],ch});
                }
            }
        }

        return dis[1];



    }
};
```

