class Solution {
public:
    int minimumCost(vector<int>& start, vector<int>& target, vector<vector<int>>& sr) {
        int ans=abs(target[0]-start[0])+abs(target[1]-start[1]);
        int n=sr.size();
        int* e_dis=new int[n];
        int* dis_dp=new int[n];

        int* st_dis=new int[n];
        int* dis_dp1=new int[n];
        vector<int> start_path(n);

        vector<int> end_path(n);
        for(int i=0;i<n;i++){
            dis_dp[i]=sr[i][4];
            dis_dp[i]+=abs(target[0]-sr[i][2])+abs(target[1]-sr[i][3]);
            end_path[i]=i;
            e_dis[i]=dis_dp[i]-sr[i][4];

            dis_dp1[i]=sr[i][4];
            dis_dp1[i]+=abs(start[0]-sr[i][0])+abs(start[1]-sr[i][1]);
            start_path[i]=i;
            st_dis[i]=dis_dp1[i]-sr[i][4];
        }
        for(int k=2;k<=sqrt(n)+1;k++){
            for(int i=0;i<n;i++){
                int res=e_dis[i];
                int n_ep=end_path[i];
                int res1=st_dis[i];
                int n_sp=start_path[i];
                for(int j=0;j<n;j++){
                    int x=abs(sr[end_path[i]][2]-sr[j][0])+abs(sr[end_path[i]][3]-sr[j][1]);
                    if(res>x+dis_dp[j]){
                        res=x+dis_dp[j];
                        n_ep=end_path[j];
                    }
                    x=abs(sr[start_path[i]][0]-sr[j][2])+abs(sr[start_path[i]][1]-sr[j][3]);
                    if(res1>x+dis_dp1[j]){
                        res1=x+dis_dp1[j];
                        n_sp=start_path[j];
                    }
                }
                dis_dp[i]+=-e_dis[i]+res;
                end_path[i]=n_ep;
