/**
 * Time Complexity: O(n* n * log(n))
 * Space Complexity: O(n * n)
 * where `n` is the length of the vector `specialRoads`
 */
class Solution {
 private:
  using node_t = pair<int, int>;

  class Hash {
   public:
    size_t operator()(const node_t &node) const noexcept {
      constexpr size_t magic = 0x9e3779b9;
      size_t ret = 0;
      ret ^= node.first + magic + (ret << 6) + (ret >> 2);
      ret ^= node.second + magic + (ret << 6) + (ret >> 2);
      return ret;
    }
  };

 public:
  int minimumCost(const vector<int> &start,
                  const vector<int> &target,
                  const vector<vector<int>> &specialRoads) {
    using pq_node_t = tuple<int, int, int>;   // {cost, x, y}
    constexpr int x_i = 0;
    constexpr int y_i = 1;
    constexpr int x1_i = 0;
    constexpr int y1_i = 1;
    constexpr int x2_i = 2;
    constexpr int y2_i = 3;
    constexpr int cost_i = 4;
    const node_t start_node(start[x_i], start[y_i]);
    const node_t target_node(target[x_i], target[y_i]);
    unordered_map<node_t, vector<pair<node_t, int>>, Hash> graph;
    graph[start_node].emplace_back(target_node, distance(start_node, target_node));
    const int n_roads = static_cast<int>(specialRoads.size());
    for (int i = 0; i < n_roads; ++i) {
      const node_t from_i(specialRoads[i][x1_i], specialRoads[i][y1_i]);
      const node_t to_i(specialRoads[i][x2_i], specialRoads[i][y2_i]);
      const int cost = specialRoads[i][cost_i];
      graph[from_i].emplace_back(to_i, cost);
      if (start_node != from_i) {
        graph[start_node].emplace_back(from_i, distance(start_node, from_i));
      }
      if (to_i != target_node) {
        graph[to_i].emplace_back(target_node, distance(to_i, target_node));
      }
      for (int j = i + 1; j < n_roads; ++j) {
        const node_t from_j(specialRoads[j][x1_i], specialRoads[j][y1_i]);
        const node_t to_j(specialRoads[j][x2_i], specialRoads[j][y2_i]);
        graph[to_i].emplace_back(from_j, distance(to_i, from_j));
        graph[to_j].emplace_back(from_i, distance(to_j, from_i));
      }
    }

    priority_queue<pq_node_t, vector<pq_node_t>, greater<>> pq;
    unordered_set<node_t, Hash> visited;
    pq.emplace(0, start_node.first, start_node.second);
    while (!pq.empty()) {
      const auto [c, x, y] = pq.top();
      pq.pop();
      node_t node(x, y);
      if (node == target_node) {
        return c;
      }
      if (visited.find(node) != visited.end()) {
        continue;
      }
      visited.emplace(node);
      for (const auto &[next, delta_c] : graph[node]) {
        if (visited.find(next) != visited.end()) {
          continue;
        }
        pq.emplace(c + delta_c, next.first, next.second);
      }
    }
    throw "impossible path";
  }

 private:
  int distance(const node_t &from, const node_t &to) {
    return abs(from.first - to.first) + abs(from.second - to.second);
  }
};
