# Intuition
Make the special roads as just another node at specialRoad[2], specialRoad[3], then the cost from some node n to some special road s will be manhattanDistance(n, s[0][1]) + s[4]. Construct a graph with that info in mind and use dijkstra algorithm to get min cost from start node to end node
# Code
```
class Solution {
public:
    // A hash function used to hash a pair of any kind
    struct hash_pair {
        template <class T1, class T2>
        size_t operator()(const pair<T1, T2>& p) const
        {
            auto hash1 = hash<T1>{}(p.first);
            auto hash2 = hash<T2>{}(p.second);

            if (hash1 != hash2) {
                return hash1 ^ hash2;             
            }

            // If hash1 == hash2, their XOR is zero.
              return hash1;
        }
    };

    int mhd(int x1, int y1, int x2, int y2){
        return abs(x1-x2) + abs(y1-y2);
    }

    int minimumCost(vector<int>& start, vector<int>& target, vector<vector<int>>& specialRoads) {
        unordered_map<pair<int, int>, vector<int>, hash_pair> um; // map val = [currMinCost, visited{0(no), 1(yes)}]
        priority_queue<pair<int, pair<int, int>>, vector<pair<int, pair<int, int>>>, greater<pair<int, pair<int, int>>>> pq;

        pq.push({0, {start[0], start[1]}});
        um[{start[0], start[1]}] = {0, 1};
        while(!pq.empty()){
            auto node = pq.top();
            pq.pop();
            int nodeCost = um[node.second][0];
            int x = node.second.first;
            int y = node.second.second;

            for (vector<int>& specialRoad : specialRoads){
                int cost = nodeCost + mhd(x, y, specialRoad[0], specialRoad[1]) + specialRoad[4];
                pair<int, int> neighbor = {specialRoad[2], specialRoad[3]};

                if (um.find(neighbor) == um.end()){
                    um[neighbor] = {cost, 0};
                }

                if (um[neighbor][1] == 0){
                    pq.push({cost, neighbor});
                    um[neighbor][1] = 1;
                }

                um[neighbor][0] = min(um[neighbor][0], cost);
            }

            int cost = nodeCost + mhd(x, y, target[0], target[1]);
            pair<int, int> neighbor = {target[0], target[1]};

            if (um.find(neighbor) == um.end()){
                um[neighbor] = {cost, 0};
            }

            if (um[neighbor][1] == 0){
                pq.push({cost, neighbor});
                um[neighbor][1] = 1;
            }

            um[neighbor][0] = min(um[neighbor][0], cost);
        }

        return um[{target[0], target[1]}][0];
    }
};
