from collections import defaultdict
import heapq 
class Solution:
    def minimumCost(self, start: List[int], target: List[int], specialRoads: List[List[int]]) -> int:
        startX, startY = start
        targetX, targetY = target
        maxVal = float('inf')
        dp = [[maxVal] * (targetY + 1) for _ in range(targetX + 1)]
        dp[startX][startY] = 0
        change = True
        while change:
            change = False
            for i in range(targetX + 1):
                for j in range(targetY + 1):
                    for road in specialRoads:
                        x1, y1, x2, y2, cost = road
                        if x2 == i and y2 == j:
                            if dp[x1][y1] + cost < dp[i][j]:
                                change = True
                                dp[i][j] = dp[x1][y1] + cost
                    if i > 0:
                        if dp[i - 1][j] + 1 < dp[i][j]:
                            dp[i][j] = dp[i - 1][j] + 1
                            change = True
                    if j > 0:
                        if dp[i][j - 1] + 1 < dp[i][j]:
                            change = True
                            dp[i][j] = dp[i][j - 1] + 1                
        return dp[targetX][targetY]
