            int fromi = map.get(fromIndex);
            int toi = map.get(toIndex);
            graph[fromi][toi] = Math.min(graph[fromi][toi], roads[i][4]);
        }

        for(int i = 0; i < cnt; i ++){
            for(int from = 0; from < cnt; from ++){
                for(int to = 0; to < cnt; to ++){
