            list.add(index);
        }
        index = String.valueOf(target[0]) + "_" + String.valueOf(target[1]);
        if (!stringMap.containsKey(index)) {
            stringMap.put(index, cnt++);
            list.add(index);
        }
        return cnt;
    }
    public static int pickTheBest(int[][] roads) {
        int r = roads.length;
        for (int i = 0; i < r; i++) {
            int[] road = roads[i];
            int distance = distance(road[0], road[1], road[2], road[3]);
            if (distance > road[4]) {
                continue;
            } else {
                swap(roads, i, --r);
                i--;
            }
        }
        return r;
    }

    public static void swap(int[][] roads, int l, int r) {
        int[] temp = roads[l];
        roads[l] = roads[r];
        roads[r] = temp;
    }

    public static int distance(int x1, int y1, int x2, int y2) {
        return Math.abs(x1 - x2) + Math.abs(y1 - y2);
    }
