# Intuition
Idea is to treat all the points described in the edges plus the starting and ending points as nodes. Then we'll just run dijakstra on this graph to get the cheapest cost. 
# Code
```
class Solution {
public:
    int minimumCost(vector<int>& start, vector<int>& target, vector<vector<int>>& specialRoads) {
        set<pair<int, int>> nodes;
        nodes.insert({start[0], start[1]});
        nodes.insert({target[0], target[1]});
        for (auto el: specialRoads) {
            nodes.insert({el[0], el[1]});
            nodes.insert({el[2], el[3]});
        }

        //reverse-index to go from coordinates of a node to its index
        map<pair<int, int>, int> rev;

        vector<pair<int, int>> vnodes;
        int ind = 0;
        for (auto el: nodes) {
            vnodes.push_back(el);
            rev[el] = ind;
            ind++;
        }

        int n = vnodes.size();
        int adj[n][n]; //cost to go from node i to node j
        for (int i = 0; i < n; i++) {
            for (int j = 0; j < n; j++) {
                adj[i][j] = abs(vnodes[i].first - vnodes[j].first) + abs(vnodes[i].second - vnodes[j].second);
            }
        }

        //Use the edges to update the cheapest costs
        for (auto k: specialRoads) {
            int i = rev[{k[0], k[1]}];
            int j = rev[{k[2], k[3]}];

            adj[i][j] = min(adj[i][j], k[4]);
        }

        priority_queue<pair<int, pair<int, int>>> pq;
        pq.push({0, {start[0], start[1]}});


        bool used[n];
        for (int i = 0; i < n; i++) {
            used[i] = false;
        }

        //Run Dijakstra
        while (!pq.empty()) {
            pair<int, pair<int, int>> cur = pq.top();
            pq.pop();

            cur.first *= -1;

            if (cur.second.first == target[0] && cur.second.second == target[1]) {
                return cur.first;
            }

            int cind = rev[cur.second];
            if (used[cind]) {
                continue;
            }

            used[cind] = true;

            for (int i = 0; i < n; i++) {
                if (used[i]) {
                    continue;
                }

                int nc = -1*(cur.first + adj[cind][i]);
                pq.push({nc, {vnodes[i].first, vnodes[i].second}});
            }
        }


        return -1;
    }
};
