public class Solution
{
    public int MinimumCost(int[] start, int[] target, int[][] specialRoads)
    {
        var n = specialRoads.Length;
        var graph = new List<(int, int)>[2 * n + 2];

        for (var i = 0; i < 2 * n + 2; i++)
            graph[i] = new List<(int, int)>();

        for (var i = 0; i < n; i++)
        {
            graph[0].Add((i + 1, Math.Abs(specialRoads[i][0] - start[0]) + Math.Abs(specialRoads[i][1] - start[1])));
            graph[i + 1].Add((n + i + 1, specialRoads[i][4]));
            graph[n + i + 1].Add((2 * n + 1, Math.Abs(specialRoads[i][2] - target[0]) + Math.Abs(specialRoads[i][3] - target[1])));

            for (var j = 0; j < n; j++)
                if (i != j)
                    graph[n + i + 1].Add((j + 1,
                        Math.Abs(specialRoads[i][2] - specialRoads[j][0]) +
                        Math.Abs(specialRoads[i][3] - specialRoads[j][1])));
        }

        var dist = new int[2 * n + 2];
        Array.Fill(dist, int.MaxValue);
        dist[0] = 0;
        var visited = new bool[2 * n + 2];

        for (var i = 0; i < 2 * n + 2; i++)
        {
            var u = -1;

            for (var j = 0; j < 2 * n + 2; j++)
                if (!visited[j] && (u == -1 || dist[j] < dist[u]))
                    u = j;

            visited[u] = true;

            foreach (var (v, w) in graph[u])
                if (dist[u] + w < dist[v])
                    dist[v] = dist[u] + w;
        }

        return Math.Min(dist[2 * n + 1], Math.Abs(target[0] - start[0]) + Math.Abs(target[1] - start[1]));
    }
}

