                if self.graph[i][k] < inf:
                    for j in range(n):
                        self.graph[i][j] = min(self.graph[i][j], self.graph[i][k] + self.graph[k][j])

    def addEdge(self, edge: List[int]) -> None:
        (u, v, w) = edge
        if self.graph[u][v] <= w:
            return
        self.graph[u][v] = w
        # Partial Floyd-Warshall for updating involved edges only
        for k in [u, v]:
            for i in range(self.n):
                for j in range(self.n):
                    self.graph[i][j] = min(self.graph[i][j], self.graph[i][k] + self.graph[k][j])

    def shortestPath(self, node1: int, node2: int) -> int:
        return self.graph[node1][node2] if self.graph[node1][node2] < inf else -1

