class Graph {
public:

    int n;
    int temp[100][100]; // Since maximum size is 100 x 100

    Graph(int n, vector<vector<int>>& edges) {

        this->n = n;    // Set number of nodes

        // Initialize cost matrix
        memset(temp,0x3f3f3f3f,sizeof temp); 

        for(int i = 0; i < edges.size(); i++) 
        // Update cost from provided edges matrix
        // edges[i][0] is fromNode edges[i][1] is toNode
        // edges[i][2] is Cost
        {
            temp[edges[i][0]][edges[i][1]] = edges[i][2];
        }
        for(int i = 0; i < n; i++)
        {
            // Cost from self is 0
            temp[i][i] = 0;
        }
        for(int k = 0; k < n; k++)
        {
            for(int i = 0; i < n; i++)
            {
                for(int j = 0; j < n; j++)
                { 
                    // Basic Floyd Warshall 
                    temp[i][j] = min(temp[i][j], temp[i][k] + temp[k][j]);
                }
            }
        }
    }

    void addEdge(vector<int> edge) {
        // When new edge is added update cost matrix
        for(int i = 0; i < n; i++)
        {
            for(int j = 0; j < n; j++)
            {
                temp[i][j] = min(temp[i][j], temp[i][edge[0]] + edge[2] + temp[edge[1]][j]);
            }
        }
    }

    int shortestPath(int node1, int node2) {
        // If node1 to node2 does not exist then return -1
        if(temp[node1][node2] == 0x3f3f3f3f)
        {
            return -1;
        }
        else
        {
            return temp[node1][node2];
        }

    }
};
