class Graph {
public:
    //Initialize graph
    vector<vector<pair<int,int>>> g;
    Graph(int n, vector<vector<int>>& edges) {
        int sz = (int)edges.size();
        g.resize(n);
       for(int i=0;i<sz;++i){
           int f = edges[i][0];
           int t = edges[i][1];
           int w = edges[i][2];
           g[f].push_back({t,w});
       } 
    }

    //Atmost 100 new esges would be introduced, max edges in a graph with total nodes n is n*(n-1).
    void addEdge(vector<int> edge) {
        g[edge[0]].push_back({edge[1],edge[2]});
    }


    //Do a Djikstra with source = node1.

    //This will pass because the max operations would be: 

    //100*O(nlogm + mlogn) m is number of edges and n is number of nodes.

    //Since m <= n*(n-1) we take the max which is n*(n-1), and max n could be 100.

    //So max operations = 100*O(100*log(100*99)+100*99*log(100)) which is nearly 2*10^6 which should pass under 1 second.

    //Will be called atmost 100 times.
    int shortestPath(int node1, int node2) {

        //Standard djikstra using priority queue. Refer cp-algorithms.

        vector<int> d((int)g.size(),2e9);
        using pii = pair<int,int>;
        priority_queue<pii,vector<pii>,greater<pii>> q;
        q.push({0, node1});
        d[node1]=0;
        while(!q.empty()){
            int v = q.top().second;
            int d_v = q.top().first;
            q.pop();
            if (d_v != d[v])
                continue;

            for (auto edge : g[v]) {
                int to = edge.first;
                int len = edge.second;

                if (d[v] + len < d[to]) {
                    d[to] = d[v] + len;
                    q.push({d[to], to});
                }
            }
        }
        if(d[node2]==2e9)
            return -1;
        return d[node2];
    }
};

