        vector<int> dist(m, INT_MAX);
        priority_queue<pair<int, int>, vector<pair<int, int>>, greater<pair<int, int>>> q;
        q.push({s, 0});
        dist[s] = 0;
        while(!q.empty()) {
            auto f = q.top();
            q.pop();

            int node = f.first;
            int di = f.second;

            for(auto nbr: adj[node]) {

                int n = nbr.first;
                int d = nbr.second;

                int total = di + d;

                if(dist[n] > total) {
                    dist[n] = total;
                    q.push({n, dist[n]});
                }
            }
        }
        return dist;
    }
    Graph(int n, vector<vector<int>>& edges) {
        m = n;
        for(auto edge: edges) {
            adj[edge[0]].push_back({edge[1], edge[2]});
        }
    }

    void addEdge(vector<int> edge) {
        adj[edge[0]].push_back({edge[1], edge[2]});
    }

    int shortestPath(int node1, int node2) {
        vector<int> ans = dijkstra(node1);
        if(ans[node2] != INT_MAX) {
            return ans[node2];
        }
        return -1;
    }
};

