class Graph {
public:

    vector<vector<pair<int,int>>>e;
    int nodes;

    Graph(int n, vector<vector<int>>& edges) {
        e.resize(n);
        for(auto &it:edges){
            e[it[0]].push_back({it[1],it[2]});
        }
        nodes=n;
    }

    void addEdge(vector<int> edge) {
        e[edge[0]].push_back({edge[1],edge[2]});

    }

    int shortestPath(int node1, int node2) {
        vector<int> dist(nodes, numeric_limits<int>::max());
        dist[node1] = 0;

        priority_queue<pair<int, int>, vector<pair<int, int>>, greater<pair<int, int>>> pq; 
        pq.push(make_pair(0, node1)); 
        while (!pq.empty()) {
            int curr_dist = pq.top().first;
            int curr_node = pq.top().second;
            pq.pop(); 

            if (curr_node == node2) {
                return curr_dist;
            }

            for (auto neighbor : e[curr_node]) {
                int next_node = neighbor.first;
                int edge_cost = neighbor.second;
                if (curr_dist + edge_cost < dist[next_node]) {
                    dist[next_node] = curr_dist + edge_cost; 
                    pq.push(make_pair(dist[next_node], next_node));
                }
            }
        }

        return -1; 
    }
};

```

