class Graph {
public:
    vector<vector<pair<int, int>>> adj;
    vector<vector<int>> dist;
    int node_cnt;

    void get_shortest_len(int src) {
        priority_queue<pair<int, int>, vector<pair<int, int>>, greater<pair<int, int>>> pq;
        vector<int> visited(node_cnt, INT32_MAX);

        pq.push({0, src});
        visited[src] = 0;

        while(!pq.empty()) {
            int node_weight = pq.top().first;
            int node = pq.top().second;
            pq.pop();
            if(visited[node] < node_weight) {
                continue;
            }

            for(int i = 0 ; i < adj[node].size() ; i++) {
                int next = adj[node][i].first;
                int next_weight = adj[node][i].second;

                if(visited[next] <= (node_weight + next_weight)) {
                    continue;
                }
                visited[next] = node_weight + next_weight;
                pq.push({node_weight + next_weight, next});
            }
        }

        for(int i = 0 ; i < node_cnt ; i++) {
            dist[src][i] = visited[i];
        }

    }
    Graph(int n, vector<vector<int>>& edges) {
        adj.clear();
        adj.resize(n);
        dist.resize(n, vector<int>(n, INT32_MAX));
        node_cnt = n;

        for(vector<int> &v : edges) {
            adj[v[0]].push_back({v[1], v[2]});
        }

        for(int i = 0 ; i < n ; i++) {
            get_shortest_len(i);
        }
    }

    void addEdge(vector<int> edge) {
        int a = edge[0];
        int b = edge[1];
        int w = edge[2];
        if(dist[a][b] <= w) {
            return;
        }
        dist[a][b] = w;

        for(int i = 0 ; i < node_cnt ; i++) {
            for(int j = 0 ; j < node_cnt ; j++) {
                if((i != j) && (dist[i][a] < INT32_MAX) && (dist[b][j] < INT32_MAX))  {
                    dist[i][j] = min(dist[i][j], dist[i][a] + dist[a][b] + dist[b][j]);
                }
            }
        }
    }

    int shortestPath(int node1, int node2) {
        return dist[node1][node2] == INT32_MAX ? -1: dist[node1][node2];
    }
};

