        int dist[]=new int[n+1];
        Arrays.fill(dist,Integer.MAX_VALUE);
        dist[node1]=0;
        PriorityQueue<int[]> pq=new PriorityQueue<>((a,b)->a[1]-b[1]);
        pq.add(new int[]{node1,0});
        while(!pq.isEmpty()){
            int u=pq.poll()[0];
            for(int p[]: adj.get(u)){
                int v=p[0];
                int w=p[1];
                if(dist[v]>dist[u]+w){
                    dist[v]=dist[u]+w;
                    pq.add(new int[]{v,dist[v]});
                }
            }
        }
        if(dist[node2]==Integer.MAX_VALUE){
                return -1;
        }
        return dist[node2];
