			this.n = n;
			adjacents = new ArrayList<>();
			for (int i = 0; i < n; i++)
				adjacents.add(new ArrayList<>());
			for (int[] edge : edges)
				adjacents.get(edge[0]).add(new int[] { edge[1], edge[2] });
		}

