			adjacents.get(edge[0]).add(new int[] { edge[1], edge[2] });
		}

		public int shortestPath(int node1, int node2) {
			PriorityQueue<int[]> q = new PriorityQueue<>((a, b) -> a[1] - b[1]);
			q.add(new int[] { node1, 0 });
			boolean[] visited = new boolean[n];
			while (!q.isEmpty()) {
				int[] point = q.poll();
				int u = point[0];
				int w = point[1];
				if (visited[u])
					continue;
				visited[u] = true;
				if (u == node2)
					return w;
				for (int[] adj : adjacents.get(u)) {
					if (!visited[adj[0]]) {
						q.offer(new int[] { adj[0], adj[1] + w });
					}
				}
			}
			return -1;
		}
	}

