class Graph {
public:
    int N=0;
    vector<vector<long long>> dis;
    Graph(int n, vector<vector<int>>& edges) 
    {
        this->N=n;
        std::vector<std::vector<long long>> Dis(100, std::vector<long long>(100, INT_MAX));
        dis=Dis;
        for(auto x: edges)
            dis[x[0]][x[1]]=x[2];

        for(int k=0;k<N;k++)
        for(int i=0;i<N;i++)   
        for(int j=0;j<N;j++)
            {if(i==j) dis[i][j]=0;
            dis[i][j]=min(dis[i][j],dis[i][k]+dis[k][j]);}
    }

    void addEdge(vector<int> edge) 
    {
        for(int i=0;i<N;i++)   
        for(int j=0;j<N;j++)
            {if(i==j) dis[i][j]=0;
            dis[i][j]=min(dis[i][j],dis[i][edge[0]]+edge[2]+dis[edge[1]][j]);}
    }


    int shortestPath(int node1, int node2) 
    {
        if (dis[node1][node2]>=INT_MAX)
            return -1;
        return dis[node1][node2];

    }
};

