class Graph {
public:
    int **grph;
    int v;
    int dijkstra(int n1,int n2, int **grph){

        vector<int> dist(v,1000000000);
        dist[n1]=0;
        vector<bool> visit(v,false);
        for(int i=0;i<v-1;i++){
            int u=-1;
            for(int k=0;k<v;k++){
                if(!visit[k] && (u==-1 || dist[k]<dist[u]))u=k;
            }
            visit[u]=true;
            for(int j=0;j<v;j++){
                if(grph[u][j]!=0 && !visit[j]){
                   dist[j]=min(dist[j],dist[u]+grph[u][j]);
                }
            }
        }
        if(dist[n2]==1000000000)return -1;
        return dist[n2];
    }
    Graph(int n, vector<vector<int>>& edges) {
        grph=new int *[n];
        for(int i=0;i<n;i++)grph[i]=new int [n];
        for(int i=0;i<n;i++){
            for(int j=0;j<n;j++){
                grph[i][j]=0;
            }
        }
        for(auto r: edges){
            grph[r[0]][r[1]]=r[2];
        }
        v=n;
    }

    void addEdge(vector<int> edge) {
        grph[edge[0]][edge[1]]=edge[2];
    }

    int shortestPath(int node1, int node2) {
        return dijkstra(node1,node2,grph);

    }
};

