class Graph {

    int[][] adj;
    int[] distance;

    public Graph(int n, int[][] edges) {
        adj = new int[n][n];
        distance = new int[n];
        for (int[] arr : adj) {
            Arrays.fill(arr, -1);
        }
        Arrays.fill(distance, Integer.MAX_VALUE);

        for (int[] edge : edges) {
            int x = edge[0];
            int y = edge[1];
            int w = edge[2];

            adj[x][y] = w;
        }
    }

    public void addEdge(int[] edge) {
        int x = edge[0];
        int y = edge[1];
        int w = edge[2];

        adj[x][y] = w;

    }

    public int shortestPath(int node1, int node2) {
        solve(distance, adj, node1, node2);
        return distance[node2] == Integer.MAX_VALUE ? -1 : distance[node2];
    }

    void solve(int[] dist, int[][] adj, int s1, int d) {
        Arrays.fill(dist, Integer.MAX_VALUE);
        dist[s1] = 0;
        Queue<int[]> pq = new PriorityQueue<>((a, b) -> Integer.compare(a[1], b[1]));
        Set<Integer> set = new HashSet<>();
        pq.add(new int[]{s1, 0});

        while (!pq.isEmpty()) {
            int[] node = pq.poll();
            int s = node[0];
            if (set.contains(s))
                continue;

            set.add(s);
            for (int i = 0; i < adj[s].length; i++) {
                if (adj[s][i] != -1 && i != s) {
                    int w = adj[s][i];
                        if (distance[s] + w < distance[i]) {
                            distance[i] = distance[s] + w;
                            pq.add(new int[]{i, distance[i]});
                        }
                }
            }
        }
    }
}

