        vector<int> d(n,INT_MAX);
        priority_queue<vector<int>,vector<vector<int>>,greater<vector<int>>> pq;
        pq.push({0,s});
        d[s]=0;
        while(!pq.empty()){
            int dis=pq.top()[0],u=pq.top()[1];
            pq.pop();
            if(u==t) return d[u];
            if(dis>d[u]) continue;
            for(auto&ev:e[u]){
                int nd=dis+ev[1],v=ev[0];
                if(nd>=d[v]) continue;
                d[v]=nd;
                pq.push({nd, v});
            }
        }
        return -1;
    }
};

