    public Graph(int n, int[][] edges) {
        l = new ArrayList[n];
        for (int i = 0; i < n; i++) {
            l[i] = new ArrayList<>();
        }
        for (int[] e: edges) {
            l[e[0]].add(new int[]{e[1], e[2]});
        }
    }

    public void addEdge(int[] edge) {
        l[edge[0]].add(new int[]{edge[1], edge[2]});
    }

    public int shortestPath(int node1, int node2) {
        int[] res = new int[l.length];
        PriorityQueue<int[]> q = new PriorityQueue<>((o1, o2) -> o1[1] - o2[1]);
        q.add(new int[]{node1, 0});
        while (!q.isEmpty()) {
            int[] k = q.poll();
            if (k[0] == node2) {
                return k[1];
            }
            if (res[k[0]] != 0) {
                continue;
            }
            res[k[0]] = k[1];
            for (int[] z: l[k[0]]) {
                if (res[z[0]] != 0) {
                    continue;
                }
                q.offer(new int[]{z[0], k[1] + z[1]});   
            }
        }
        return -1;
    }
}

