class Graph {
    class pair{
        int vertex;
        int weight;
        public pair(int vertex,int weight){
            this.vertex=vertex;
            this.weight=weight;
        }
    }
    int n;
    ArrayList<ArrayList<pair>> adj;
    public Graph(int n, int[][] edges) {
        this.n=n;
        this.adj = new ArrayList<>();
        for(int i=0;i<=n;i++) adj.add(new ArrayList<>());
        for(int i=0;i<edges.length;i++){
            adj.get(edges[i][0]).add(new pair(edges[i][1],edges[i][2]));
        }
    }

    public void addEdge(int[] edge) {
        adj.get(edge[0]).add(new pair(edge[1],edge[2]));
    }

    public int shortestPath(int node1, int node2) {
        int[] dist = new int[n+1];
        Arrays.fill(dist,Integer.MAX_VALUE);
        PriorityQueue<pair> pq = new PriorityQueue<>((x,y) -> x.weight - y.weight);
        dist[node1] = 0;
        pq.offer(new pair(node1,0));
        while(!pq.isEmpty()){
            int vertex = pq.peek().vertex;
            int weight = pq.peek().weight;
            pq.poll();
            if(vertex==node2) return weight;
            for(pair it:adj.get(vertex)){
                if(dist[it.vertex]>weight+it.weight){
                    dist[it.vertex] = weight+it.weight;
                    pq.offer(new pair(it.vertex,dist[it.vertex]));
                }
            }
        }
        return -1;
    }
}

