                    distanceFromStart[next.ID] = current.costFromStart + next.costOfEdge;
                    minHeap.emplace(next.ID, distanceFromStart[next.ID]);
                }
            }
        }

        return distanceFromStart[goal] != INT_MAX ? distanceFromStart[goal] : NO_PATH_EXISTS;
    }
};
