class Graph {
public:
vector<vector<int>> cost;
int n=-1;
    Graph(int n, vector<vector<int>>& edges) {
        this->n=n;
        cost.resize(n,vector<int>(n,1e8));
        for(int i=0;i<n;i++) cost[i][i]=0;
        for(auto& ele: edges){
            cost[ele[0]][ele[1]]=ele[2];
        }
        for(int k=0;k<n;k++){
            for(int i=0;i<n;i++){
                for(int j=0;j<n;j++){
                    if(cost[i][j]>cost[i][k]+cost[k][j]){
                        cost[i][j]=cost[i][k]+cost[k][j];
                    }
                }
            }
        }
    }

    void addEdge(vector<int> ele) {
        for(int i=0;i<n;i++){
            for(int j=0;j<n;j++){
                if(cost[i][j]>cost[i][ele[0]]+cost[ele[1]][j]+ele[2]){
                    cost[i][j]=cost[i][ele[0]]+cost[ele[1]][j]+ele[2];
                }
            }
        }
    }

    int shortestPath(int node1, int node2) {
        return (cost[node1][node2]!=1e8)?cost[node1][node2]:(-1);
    }
};

