    vector<vector<int>> g;

    Graph(int n, vector<vector<int>>& edges): g (n, vector<int> (n, INT_MAX)) {
        for (vector<int> & e: edges) g[e[0]][e[1]] = e[2];
    }

    void addEdge(vector<int> edge) {
        g[edge[0]][edge[1]] = edge[2];
    }

    int shortestPath(int node1, int node2) {
        int nodenum = this -> g.size();
        vector<bool> visited (nodenum, false);
        vector<int> dis (nodenum, INT_MAX);
        dis[node1] = 0;
        while (true) {
            int nearest = -1;
            for (int i = 0; i < nodenum; i += 1) {
                if (!visited[i] && (nearest == -1 || dis[i] < dis[nearest])) nearest = i;
            }
            if (dis[nearest] == INT_MAX) return -1; // all nodes reachable from the start are updated
            else if (nearest == node2) return dis[node2];
            visited[nearest] = true;
            for (int i = 0; i < nodenum; i += 1) {
                if (g[nearest][i] != INT_MAX) dis[i] = min(dis[i], dis[nearest] + g[nearest][i]);
            }
        }
    }
