class Graph {
public:
    Graph(int n, vector<vector<int>>& edges) {
        N = n;
        for (int i = 0; i < n; i++) {
            for (int j = 0; j < n; j++) {
                if (i != j) {
                    dp[i][j] = 1e8;
                } else {
                    dp[i][j] = 0;
                }
            }
        }
        for (auto e : edges) {
            dp[e[0]][e[1]] = e[2];
        }
        // floyd 算法
        for (int k = 0; k < n; k++) {
            for (int i = 0; i < n; i++) {
                if (i == k) continue;
                for (int j = 0; j < n; j++) {
                    if (i == j or j == k) continue;
                    dp[i][j] = min(dp[i][j], dp[i][k] + dp[k][j]);
                }
            }
        }
    }

    void addEdge(vector<int> edge) {
        dp[edge[0]][edge[1]] = min(dp[edge[0]][edge[1]], edge[2]);
        // update the min distance from edge[0] to other node
        for (int i = 0; i < N; i++) {
            if (i == edge[0] or i == edge[1]) continue;
            dp[edge[0]][i] = min(dp[edge[0]][i], edge[2] + dp[edge[1]][i]);
        }
        // update the min distance from any node to other node using edge[0]
        for (int i = 0; i < N; i++) {
            for (int j = 0; j < N; j++) {
                if (i == j or j == edge[0] or i == edge[0]) continue;
                dp[i][j] = min(dp[i][j], dp[i][edge[0]] + dp[edge[0]][j]);
            }
        }
    }

    int shortestPath(int node1, int node2) {
        return dp[node1][node2] == 1e8 ? -1 : dp[node1][node2];
    }
private:
    int N;
    int dp[105][105];
};

