class Graph {
public:
    int s;
    unordered_map<int,vector<pair<int,int>>> mp;

    Graph(int n, vector<vector<int>>& edges) 
    {
        s = n;
        for(auto i:edges)
        {
            int u = i[0];
            int v = i[1];
            int wt = i[2];
            mp[u].push_back({v,wt});
        }

    }

    void addEdge(vector<int> edge) 
    {
        int u = edge[0];
        int v = edge[1];
        int wt = edge[2];
        mp[u].push_back({v,wt});       

    }

    int shortestPath(int node1, int node2) 
    {
        //Applying dijkstra's
        priority_queue<pair<int,int>,vector<pair<int,int>>,greater<pair<int,int>>> q;
        //we will be pushing 0 distance from node1 initially. 
        q.push({0,node1});
        vector<int> dist(s,INT_MAX);
        dist[node1]=0;

        while(!q.empty())
        {
            int dis = q.top().first;
            int node = q.top().second;
            q.pop();
            for(auto i:mp[node])
            {
                int adjNode = i.first;
                int wt = i.second;
                if(dis+wt<dist[adjNode])
                {
                    dist[adjNode]=dis+wt;
                    q.push({dist[adjNode],adjNode});
                }
            }
        }
        return dist[node2]==INT_MAX?-1:dist[node2];



    }
};

