class Graph {
public:
    vector<int> Adj[100];
    int Dist[101][101];
    int N;
    Graph(int n, vector<vector<int>>& edges) {
        N = n;
        for(int i=0;i<n;i++)
        {
            for(int j=0;j<n;j++)
                Dist[i][j] = INF;
        }
        for(auto &x : edges)
        {
            Adj[x[0]].push_back(x[1]);
            Dist[x[0]][x[1]] = x[2];
        }
    }

    void addEdge(vector<int> x) {
        Adj[x[0]].push_back(x[1]);
        Dist[x[0]][x[1]] = x[2];
    }

    int shortestPath(int node1, int node2){
        if(node1 == node2)return 0;
        priority_queue<pi, vector<pi>, greater<pi>> pq;
        pq.push({0, node1});
        vector<int> IsVisited(N, INF);
        IsVisited[node1] = 0;
        int ans = INF;
        while(!pq.empty())
        {
            pi a = pq.top();
            pq.pop();
            int idx = a.second, dist = a.first;
            for(auto &x : Adj[idx])
            {
                if(Dist[idx][x] + dist < IsVisited[x]){pq.push({Dist[idx][x] + dist, x});IsVisited[x] = Dist[idx][x] + dist;}
                if(x == node2)ans  = min(dist + Dist[idx][x], ans);
            }
        }
        return (ans == INF) ? -1 : ans;
    }
};

